import React, { useState, useReducer } from "react";
import Modal from "./Modal";
import { data } from "../../../data";
// reducer function

const reducer = (state, action) => {
  if (action.type === "TESTING") {
    const addPeople = [...state.people, action.addPeople];
    return {
      ...state,
      people: addPeople,
      isModalOpen: true,
      ModalContent: "item added",
    };
  }
  if (action.type === "NO_ITEM") {
    return {
      ...state,
      isModalOpen: true,
      ModalContent: "please add item",
    };
  }
  if (action.type === "CLOSE_MODAL") {
    return { ...state, isModalOpen: false };
  }
  throw new Error("no matching action type");
};
const defaultState = {
  people: [],
  isModalOpen: false,
  ModalContent: " ",
};

const Index = () => {
  const [name, setName] = useState("");
  const [state, dispatch] = useReducer(reducer, defaultState);
  const handleSubmit = (e) => {
    e.preventDefault();
    if (name) {
      console.log("hello world");

      const newPeople = { id: new Date().getTime().toString(), name };
      dispatch({ type: "TESTING", addPeople: newPeople });
      setName(" ");
    } else {
      dispatch({ type: "NO_ITEM" });
    }
  };
  const removeModal = () => {
    dispatch({ type: "CLOSE_MODAL" });
  };

  return (
    <>
      {state.isModalOpen && (
        <Modal closeModal={removeModal} modalContent={state.ModalContent} />
      )}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <button type="submit"> submit</button>
      </form>
      {state.people.map((person) => {
        return (
          <div key={person.id} className="item">
            <h2>{person.name}</h2>;
          </div>
        );
      })}
    </>
  );
};

export default Index;
