import React, { useState, useEffect } from "react";
// by default runs after every re-render
// cleanup function
// second parameter
const UseEffectBasics = () => {
  useEffect(() => {
    console.log("call useEffect");
  });

  return <></>;
};

export default UseEffectBasics;
